  computed: {
    activeCourse: (state) => state.coursesList.find((t) => t.active),
    firstTeacher: (state) => state.activeCourse.teachers[0],
    secondTeacher: (state) => state.activeCourse.teachers[1] || false,
    firstPhoto: (state) => state.filePath(state.firstTeacher ? state.firstTeacher.photos[0] : ''),
    secondPhoto: (state) =>
      state.secondTeacher
        ? state.filePath(state.firstTeacher ? state.secondTeacher.photos[0] : '')
        : state.filePath(state.firstTeacher ? state.firstTeacher.photos[1] : ''),
    video: (state) => {
      return state.filePath(state.activeTeacher.video[0])
    }
  },
  methods: {
    chooseCourse(course) {
      this.coursesList.map((t) => (t.active = false))
      course.active = true
    },
    setActiveTeacher(teacher) {
      this.activeTeacher = teacher
      this.showVideo = true
    }
  }
